<script>
    import {mapState} from "vuex";

    import profile from './components/profile'
    import security from './components/security'
    import AdjustSubtitles from './components/adjust-subtitles'
    import HistoryWatched from './components/viewing-history'
    import language from './components/language'

    export default  {

        data() {
            return {
                ShowSettingComponent: 'profile'
            }
        },

        components: {
            profile,
            security,
            language,
            'adjust-subtitles': AdjustSubtitles,
            'history-watched': HistoryWatched
        },

        computed: mapState({
            SIGNUP_STEP: state => state.register.SIGNUP_STEP,
            LIST_SERIES_FOR_SUBSCRIBE: state => state.register.LIST_SERIES_FOR_SUBSCRIBE,
            ErrorMessage: state => state.register.ErrorMessage,
            ButtonLoading: state => state.register.ButtonLoading
        }),

        methods: {
            HIDE_PROFILE_MODAL() {
                this.$store.commit('SHOW_PROFILE_MODAL', false)
            },

        }


    }
</script>
